queries.create:

CREATE PROCEDURE dbo.logChanges
    @field      VARCHAR(64),
    @old_val    VARCHAR(512),
    @new_val    VARCHAR(512),
    @id         BIGINT
AS
BEGIN
    IF (@old_val != @new_val)
    BEGIN
        DECLARE @login_name VARCHAR(128) = (
            SELECT login_name
            FROM sys.dm_exec_sessions
            WHERE session_id = @@SPID
        )

        INSERT INTO users_audit_modification
        (
            user_id,
            field_name,
            old_value,
            new_value,
            modified_by,
            modified_date
        )
        VALUES
        (
            @id,
            @field,
            @old_val,
            @new_val,
            @login_name,
           GETDATE()
        )
    END
END
